mapper.update:
UPDATE ZTHH_ERRORHIST
SET ERROR_MSG = #{errorMsg}, SOLUTION_MSG = #{solutionMsg}, update_id = #{updateId}, update_dt = CURRENT_TIMESTAMP
WHERE id = #{id}

mapper.select:
SELECT ERROR_MSG, SOLUTION_MSG, id, del_yn, create_id, create_dt, update_id, update_dt FROM ZTHH_ERRORHIST
WHERE id = #{id}
ORDER BY update_dt, create_dt
