mapper.update:
UPDATE ZTHH_MENUCONTROL
SET URL = #{url}, METHOD = #{method}, log_yn = #{logYn}, ROLE_ID = #{roleId}, update_id = #{updateId}, update_dt = CURRENT_TIMESTAMP
WHERE id = #{id}

mapper.select:
SELECT URL, METHOD, ROLE_ID, id, log_yn, del_yn, create_id, create_dt, update_id, update_dt FROM ZTHH_MENUCONTROL
WHERE id = #{id}
ORDER BY update_dt, create_dt

SELECT URL, METHOD, ROLE_ID, id, log_yn, del_yn, create_id, create_dt, update_id, update_dt FROM ZTHH_MENUCONTROL
WHERE ROLE_ID = #{roleId}
AND del_yn='N'
