mapper.select:
SELECT id, title, secret, created_at FROM posts WHERE del_yn=0
AND LOWER(title) LIKE '%'||LOWER(#{search.title.value})||'%'
AND secret = #{search.isSecret.value}
AND TO_CHAR(created_at, 'YYYY-MM-DD') >= #{search.startDate.value}
AND TO_CHAR(created_at, 'YYYY-MM-DD') <= #{search.endDate.value}
ORDER BY ${orderColumnName} ${orderDirection} OFFSET #{offset} ROWS FETCH NEXT #{limit} ROWS ONLY

mapper.update:

UPDATE posts SET title=#{title},
				 secret=#{isSecret}
WHERE id = #{id}

mapper.select:
        SELECT
            COUNT(*)
        FROM
            posts
        WHERE del_yn=0

	            AND LOWER(title) LIKE '%'||LOWER(#{search.title.value})||'%'


	            AND secret = #{search.isSecret.value}


		      	AND TO_CHAR(created_at, 'YYYY-MM-DD') >= #{search.startDate.value}


		      	AND TO_CHAR(created_at, 'YYYY-MM-DD') <= #{search.endDate.value}
